                    var titles = ['Articles', 'Videos', 'Photos', 'Sound', 'Map', 'Form', 'About', 'Contact us'];
                    var tears = ['data:image/svg+xml;base64,', 'data:image/svg+xml;base64,', 'data:image/svg+xml;base64,', 'data:image/svg+xml;base64,', 'data:image/svg+xml;base64,', 'data:image/svg+xml;base64,', 'data:image/svg+xml;base64,', 'data:image/svg+xml;base64,'];
                    $.each(sections, function(index, value) {
                        $('#content-mcms a[rel=' + index + ']')
                                .html('<i class=\"' + icons[index] + '\"></i>')
                                .css('background-color', colors[index])
                                .append('<label style=\"color: ' + colors[index] + '\">' + titles[index] + '</label>')
                                .append('<img src="' + tears[index] + '" alt="" />');
                    });
                }
